package main

import (
	"testing"
)

func TestLongestPalindrome(t *testing.T) {
	cases := []struct {
		Input    string
		Expected string
	}{
		{Input: "babad", Expected: "bab"},
		{Input: "cbbd", Expected: "bb"},
		{Input: "a", Expected: "a"},
		{Input: "bb", Expected: "bb"},
		{Input: "",
			Expected: "qgjjgq"},
		{Input: "",
			Expected: ""},
	}

	for _, x := range cases {
		actual := longestPalindrome(x.Input)
		if actual != x.Expected {
			t.Errorf("Input=%s; Expected=%s; Actual=%s", x.Input, x.Expected, actual)
		}
	}
}
